import React, { useState } from 'react';

function Toggle() {
    const [languageOption, setLanguageOption] = useState('EN');

    function toggleLanguage() {
        if (languageOption === 'EN') {
            setLanguageOption('FR');
        } else {
            setLanguageOption('EN');
        }
    }

    return (
        <section className="toggler-section">
            <p>
                <span className="english-button">{languageOption === 'EN' ? 'EN' : 'FR'}</span> | <span onClick={toggleLanguage} className="french-button">{languageOption === 'EN' ? 'FR' : 'EN'}</span>
            </p>
        </section>
    );
}

export default Toggle;
