import React, { useEffect } from 'react';
import '../styles/Portfolio.scss';
import img1 from '../assets/p1.jpg';
import img2 from '../assets/p2.png';
import img3 from '../assets/p3.png';
import img4 from '../assets/p4.jpg';
import img5 from '../assets/p5.jpg';
import img6 from '../assets/p6.jpg';
import img7 from '../assets/p7.jpg';
import img8 from '../assets/p8.jpg';
import img9 from '../assets/p9.png';
import { motion, useAnimation } from 'framer-motion';
import { useInView } from 'react-intersection-observer'; // For triggering animation on scroll

function Portfolio() {
    const [ref1, inView1] = useInView({ threshold: 0.1 });
    const controls1 = useAnimation();

    useEffect(() => {
        if (inView1) {
            controls1.start({ scale: 1 });
        } else {
            controls1.start({ scale: 0.9 });
        }
    }, [controls1, inView1]);

    const [ref2, inView2] = useInView({ threshold: 0.1 });
    const controls2 = useAnimation();

    useEffect(() => {
        if (inView2) {
            controls2.start({ scale: 1 });
        } else {
            controls2.start({ scale: 0.9 });
        }
    }, [controls2, inView2]);

    const [ref3, inView3] = useInView({ threshold: 0.1 });
    const controls3 = useAnimation();

    useEffect(() => {
        if (inView3) {
            controls3.start({ scale: 1 });
        } else {
            controls3.start({ scale: 0.9 });
        }
    }, [controls3, inView3]);

    return (
        <section className="portfolio-section" id="portfolio">
            <h2 className="portfolio-title">PORTFOLIO</h2>
            <div className="image-block1">
                <motion.img
                    ref={ref1} // Add the ref to the first image
                    className="portfolio-image"
                    src={img4}
                    alt='image-4'
                    initial={{ scale: 0.9 }}
                    animate={controls1}
                    transition={{ duration: 0.5 }}
                />
                <motion.img
                    ref={ref2} // Add the ref to the second image
                    className="portfolio-image"
                    src={img2}
                    alt='image-2'
                    initial={{ scale: 0.9 }}
                    animate={controls2}
                    transition={{ duration: 0.5 }}
                />
                <motion.img
                    ref={ref3} // Add the ref to the third image
                    className="portfolio-image center-img"
                    src={img3}
                    alt='image-3'
                    initial={{ scale: 0.9 }}
                    animate={controls3}
                    transition={{ duration: 0.5 }}
                />
            </div>
            <div className="image-block1">
                <motion.img
                    className="portfolio-image center-img"
                    src={img5}
                    alt='image-5'
                    initial={{ scale: 0.9 }}
                    animate={controls1}
                    transition={{ duration: 0.5 }}
                />
                <motion.img
                    className="portfolio-image"
                    src={img1}
                    alt='image-1'
                    initial={{ scale: 0.9 }}
                    animate={controls2}
                    transition={{ duration: 0.5 }}
                />
                <motion.img
                    className="portfolio-image"
                    src={img6}
                    alt="image-6"
                    initial={{ scale: 0.9 }}
                    animate={controls3}
                    transition={{ duration: 0.5 }}
                />
            </div>
            <div className="image-block3"> 
                <motion.img
                    className="portfolio-image"
                    src={img8}
                    alt='image-8'
                    initial={{ scale: 0.9 }}
                    animate={controls1}
                    transition={{ duration: 0.5 }}
                />       
                <motion.img
                    className="portfolio-image center-img"
                    src={img7}
                    alt='image-7'
                    initial={{ scale: 0.9 }}
                    animate={controls2}
                    transition={{ duration: 0.5 }}
                />
                <motion.img
                    className="portfolio-image"
                    src={img9}
                    alt='image-9'
                    initial={{ scale: 0.9 }}
                    animate={controls3}
                    transition={{ duration: 0.5 }}
                />
            </div>
        </section>
    );
}

export default Portfolio;
