import React, { useState } from 'react';
import '../styles/Navbar.scss';
import Toggler from './Toggler';
import logo from '../assets/logo.png';

function Navbar() {
    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const handleMenuClick = () => {
        setIsMenuOpen(!isMenuOpen);
        if (isMenuOpen) {
            document.body.classList.remove('no-scroll');
        } else {
            document.body.classList.add('no-scroll');
        }
    };

    // Function to handle scrolling to sections
    const handleScrollToSection = (sectionId) => {
        const section = document.querySelector(sectionId);
        if (section) {
            window.scrollTo({
                top: section.offsetTop, // Adjust scroll position
                behavior: 'smooth' // Smooth scroll behavior
            });
        }
        setIsMenuOpen(false); // Close the menu after clicking
    };

    return (
        <section className={isMenuOpen ? 'navbar-section show' : 'navbar-section'}>
            <div className={isMenuOpen ? 'menu-icon open' : 'menu-icon'} onClick={handleMenuClick}>
                <div className="bar1"></div>
                <div className="bar2"></div>
                <div className="bar3"></div>
            </div>
            <div className="navbar-flexbox">
                <div className="navbar-logo">
                    <img className="logo-image" src={logo} alt="nature sculpt logo" />
                </div>
                <div className={isMenuOpen ? 'navbar-links show' : 'navbar-links'}>
                    <a href="#home" onClick={() => handleScrollToSection('#home')}>HOME</a>
                    <a href="#services" onClick={() => handleScrollToSection('#services')}>OUR SERVICES</a>
                    <a href="#about" onClick={() => handleScrollToSection('#about')}>ABOUT US</a>
                    <a href="#portfolio" onClick={() => handleScrollToSection('#portfolio')}>PORTFOLIO</a>
                    <Toggler />
                </div>
            </div>
        </section>
    );
}

export default Navbar;
