import React, { useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { useInView } from 'react-intersection-observer'; // For triggering animation on scroll
import '../styles/Portfolio.scss';
import img1 from '../assets/p1.jpg';
import img2 from '../assets/p2.png';
import img3 from '../assets/p3.png';
import img4 from '../assets/p4.jpg';
import img5 from '../assets/p5.jpg';
import img6 from '../assets/p6.jpg';
import img7 from '../assets/p7.jpg';
import img8 from '../assets/p8.jpg';
import img9 from '../assets/p9.png';

function Portfolio() {
    // Animation controls for each row
    const controls1 = useAnimation();
    const controls2 = useAnimation();
    const controls3 = useAnimation();

    // In view checks for each row
    const [ref1, inView1] = useInView({ threshold: 0.1 });
    const [ref2, inView2] = useInView({ threshold: 0.1 });
    const [ref3, inView3] = useInView({ threshold: 0.1 });

    useEffect(() => {
        if (inView1) {
            controls1.start({ scale: 1, y: 0 });
        } else {
            controls1.start({ scale: 0.8, y: 20 });
        }
    }, [controls1, inView1]);

    useEffect(() => {
        if (inView2) {
            controls2.start({ scale: 1, y: 0 });
        } else {
            controls2.start({ scale: 0.8, y: 20 });
        }
    }, [controls2, inView2]);

    useEffect(() => {
        if (inView3) {
            controls3.start({ scale: 1, y: 0 });
        } else {
            controls3.start({ scale: 0.8, y: 20 });
        }
    }, [controls3, inView3]);

    return (
        <section className="portfolio-section" id="portfolio">
            <h2 className="portfolio-title">PORTFOLIO</h2>
            <motion.div 
                className="image-block1" 
                ref={ref1} 
                initial={{ scale: 0.8, y: 20 }} 
                animate={controls1} 
                transition={{ duration: 0.5 }}
            >
                <img src={img4} alt='image-4' />
                <img src={img2} alt='image-2' />
                <img className="center-img" src={img3} alt='image-3' />
            </motion.div>

            <motion.div 
                className="image-block1" 
                ref={ref2} 
                initial={{ scale: 0.8, y: 20 }} 
                animate={controls2} 
                transition={{ duration: 0.5, delay: 0.3 }} // Delay for staggered effect
            >
                <img className="center-img" src={img5} alt='image-5' />
                <img src={img1} alt='image-1' />
                <img src={img6} alt="image-6" />
            </motion.div>

            <motion.div 
                className="image-block3" 
                ref={ref3} 
                initial={{ scale: 0.8, y: 20 }} 
                animate={controls3} 
                transition={{ duration: 0.5, delay: 0.6 }} // Further delay for staggered effect
            >
                <img src={img8} alt='image-8' />       
                <img className="center-img" src={img7} alt='image-7' />
                <img src={img9} alt='image-9' />
            </motion.div>
        </section>
    );
}

export default Portfolio;
