import React, { useState } from 'react'
import '../styles/Navbar.scss'
import Toggler from './Toggler'
import logo from '../assets/logo.png'

function Navbar() {

    const [isMenuOpen, setIsMenuOpen] = useState(false);

    const handleMenuClick = () => {
        setIsMenuOpen(!isMenuOpen);

        (isMenuOpen ? document.body.classList.remove('no-scroll') : document.body.classList.add('no-scroll'));

    };

    return(
        <section className={isMenuOpen ? 'navbar-section show' : 'navbar-section'}>
            <div className={isMenuOpen ? 'menu-icon open' : 'menu-icon'} onClick={handleMenuClick}>
                    <div className="bar1"></div>
                    <div className="bar2"></div>
                    <div className="bar3"></div>
            </div>
            <div className="navbar-flexbox">
                <div className="navbar-logo">
                    <img className="logo-image" src={logo} alt="nature sculpt logo"></img>
                </div>
                <div className={isMenuOpen ? 'navbar-links show' : 'navbar-links'}>
                    <a href=".about-section" >HOME</a>
                    <a href="" >OUR SERVICES</a>
                    <a href="" >ABOUT US</a>
                    <a href=".portfolio-section" >PORTFOLIO</a>
                    <Toggler />
                </div>
            </div>
        </section>
    ) 
}

export default Navbar
